import React, { useState } from "react";
import LoginPage from "./components/LoginPage";

function App() {
  // State to hold the logged-in user's information
  const [user, setUser] = useState(null);

  // Handle login by setting the user information
  const handleLogin = (username) => {
    setUser(username);
    localStorage.setItem("user", username); // Optionally, store user in localStorage
    console.log("Logged in as:", username);
  };

  // Handle logout by clearing the user info
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("user");
    console.log("Logged out!");
  };

  return (
    <div>
      {user ? (
        <div>
          <h2>Welcome, {user}!</h2>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <LoginPage onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
