import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import MessageList from "./MessageList";
import ChatInput from "./ChatInput";
import ChatHeader from "./ChatHeader";
import img1 from "../assets/back1.jpg";
import { Box, Container } from "@mui/material";

const socket = io("http://localhost:5000");

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [user, setUser] = useState("");

  useEffect(() => {
    // Retrieve logged-in user's data from localStorage
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser)); // Set user if they are logged in
    } else {
      // Redirect to login page if user is not logged in
      alert("Please log in first!");
    }
  }, []);

  useEffect(() => {
    // Listen for incoming messages
    socket.on("receiveMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (newMessage.trim()) {
      const userMessage = { user: user.username, text: newMessage };

      // Emit the message to the server
      socket.emit("sendMessage", userMessage);

      // Add the user's message to the state
      setMessages((prev) => [...prev, userMessage]);

      setNewMessage("");
    }
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        backgroundImage: `url(${img1})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <Container
        maxWidth="sm"
        sx={{
          display: "flex",
          flexDirection: "column",
          height: "90vh",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)",
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          backdropFilter: "blur(10px)",
        }}
      >
        {/* Chat Header */}
        <ChatHeader user={user.username} />

        {/* Message List */}
        <MessageList messages={messages} user={user.username} />

        {/* Chat Input */}
        <ChatInput
          newMessage={newMessage}
          setNewMessage={setNewMessage}
          sendMessage={sendMessage}
        />
      </Container>
    </Box>
  );
}

export default Chat;
